import anime from 'animejs'
import lottie from 'lottie-web'
import * as cardsAnimation from '~/assets/lottie/cards.json'
import { onMounted, ref } from 'vue'

const planningSvg = ref(null)
const pokerSvg = ref(null)
const lottieWrapper = ref(null)
onMounted(() => {
  const planningPaths = planningSvg.value.querySelectorAll('path')
  animatePaths(planningPaths, 600)
  const pokerPaths = pokerSvg.value.querySelectorAll('path')
  animatePaths(pokerPaths, 600)
  const cards = lottie.loadAnimation({
    container: lottieWrapper.value,
    renderer: 'svg',
    loop: false,
    autoplay: true,
    animationData: JSON.parse(JSON.stringify(cardsAnimation)),
  })
  cards.playSegments([0, 28], true)
})

const animatePaths = (paths, totalDuration, fillDuration = 350) => {
  anime
    .timeline()
    .add({
      targets: paths,
      strokeDashoffset: [anime.setDashoffset, 0],
      easing: 'easeInOutSine',
      duration: 400,
      delay: (el, i) => (i * totalDuration) / paths.length,
    })
    .add({
      targets: paths,
      fill: ['rgba(0,0,0,0)', 'rgba(255, 255, 255, 0.87)'],
      duration: 300,
      delay: (el, i) => (i * fillDuration) / paths.length,
      easing: 'linear',
    })
}
